public class MathFun {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		float f=20.33f;
		double d=30.5;
		long l=3398;
		System.out.println("Int "+Math.abs(f));

		System.out.println("Float "+Math.abs(a));

		System.out.println("Double "+Math.abs(d));

		System.out.println("Long "+Math.abs(l));
		System.out.println("Expolnal value 339 "+Math.exp(339));
		System.out.println("Max Number Between 33 and 22.4"+Math.max(33,22.4));
		System.out.println("Power value 33 and 2"+Math.pow(33,2));
		System.out.println("sqr value 33 "+Math.sqrt(33));
		System.out.println("Round Float Number 22.5f   "+Math.round(22.5f));
		System.out.println("Round Double Number 22.22   "+Math.round(22.22));
		
		
		
		
		
		
		
	}

}
